import { useEffect, useState } from 'react'
import './EndQuiz.css'

export default function EndQuiz({pshow}) {

    const [show, setShow ] = useState(false);

    useEffect(() => {
        setShow(pshow)
    }, [pshow])

    return (
        <div
         style = {{
            visibility : show ? "visible" : "hidden",
            opacity : show ?  "1" : "0"
         }} 
            className='endquiz'>
            <div className="congrats">
                <h2>Congratulations!</h2>
            </div>
            <div className="content">
                <p>You have completed the quiz. 
                <br></br> You got : 
                <strong> 2 </strong>
                out of 
                <strong> 8 </strong>
                questions right.
                 </p>

                 <button className='btn'>Restart</button>
            </div>

            
        </div>
    )
}
